import makeWASocket, { useMultiFileAuthState, fetchLatestBaileysVersion, delay } from '@whiskeysockets/baileys';
import pino from 'pino';
import { join } from 'path';
import { os } from 'os';

async function pair() {
    const authDir = join(process.env.HOME, '.openclaw/credentials/whatsapp/default');
    const { state, saveCreds } = await useMultiFileAuthState(authDir);
    const { version } = await fetchLatestBaileysVersion();

    const sock = makeWASocket.default({
        auth: state,
        version,
        logger: pino({ level: 'silent' }),
        browser: ["Ubuntu", "Chrome", "20.0.04"],
        printQRInTerminal: false
    });

    sock.ev.on('creds.update', saveCreds);

    sock.ev.on('connection.update', (update) => {
        const { connection, lastDisconnect } = update;
        if (connection === 'close') {
            const shouldReconnect = (lastDisconnect?.error)?.output?.statusCode !== 401;
            if (shouldReconnect) pair();
        } else if (connection === 'open') {
            console.log('\\n\\n✅ SUCCESS! WhatsApp is connected.\\n\\n');
            process.exit(0);
        }
    });

    await delay(5000);
    const code = await sock.requestPairingCode("+33663252842");
    console.log("\\n\\n************************************");
    console.log("FRESH PAIRING CODE:", code);
    console.log("************************************\\n\\n");
    console.log("Please enter this code on your phone NOW.");
}

pair();
EOF

