import makeWASocketPkg from '@whiskeysockets/baileys';
const makeWASocket = makeWASocketPkg.default;
const { useMultiFileAuthState, fetchLatestBaileysVersion, delay } = makeWASocketPkg;
import pino from 'pino';
import { join } from 'path';
import { homedir } from 'os';

async function pair() {
    console.log("Starting pairing process for +33663252842...");
    const authDir = join(homedir(), '.openclaw/credentials/whatsapp/default');
    const { state, saveCreds } = await useMultiFileAuthState(authDir);
    const { version } = await fetchLatestBaileysVersion();

    const sock = makeWASocket({
        auth: state,
        version,
        logger: pino({ level: 'silent' }),
        browser: ['Ubuntu', 'Chrome', '20.0.04'],
        printQRInTerminal: false
    });

    sock.ev.on('creds.update', saveCreds);

    sock.ev.on('connection.update', (update) => {
        const { connection, lastDisconnect } = update;
        if (connection === 'close') {
            console.log('Connection closed. If you did not enter the code yet, please wait for retry.');
            // Only retry if not logged out
            if (lastDisconnect?.error?.output?.statusCode !== 401) {
                setTimeout(pair, 5000);
            }
        } else if (connection === 'open') {
            console.log('\n\n✅ SUCCESS! WhatsApp is connected.\n\n');
            process.exit(0);
        }
    });

    await delay(5000);
    try {
        const code = await sock.requestPairingCode('33663252842');
        console.log('\n\n************************************');
        console.log('YOUR WHATSAPP PAIRING CODE:', code);
        console.log('************************************\n\n');
        console.log('Enter this code on your phone now.');
    } catch (e) {
        console.error('Error requesting pairing code:', e.message);
    }
}

pair();

