import asyncio
import logging
import os
import json
from threading import Thread
import aiohttp
import requests
import schedule
import time
from fastapi import FastAPI, Request, BackgroundTasks

# --- CONFIGURATION ---
OPENAI_API_KEY = os.environ.get("OPENAI_API_KEY")
OPENAI_URL = "https://api.openai.com/v1/chat/completions"

RADARR_URL = "http://gluetun:7878/api/v3"
RADARR_API_KEY = "75ed9e2e3922442c8c1e012e47bc0906"

SONARR_URL = "http://gluetun:8989/api/v3"
SONARR_API_KEY = "dfa7da413b48480097f6a399080be393"

WUZAPI_URL = "http://wuzapi:8080"
WUZAPI_TOKEN = "jules_token_2026"
AUTHORIZED_NUMBER = "33663252842"

app = FastAPI()
logging.basicConfig(level=logging.INFO)
logger = logging.getLogger("assistant")

# --- OUTILS (RADARR) ---
async def add_movie_radarr(title):
    search_url = f"{RADARR_URL}/movie/lookup?term={title}&apiKey={RADARR_API_KEY}"
    logger.info(f"Searching for movie: {title}")

    try:
        async with aiohttp.ClientSession() as session:
            async with session.get(search_url, timeout=15) as resp:
                if resp.status != 200:
                    return f"Erreur Radarr (Code {resp.status})."

                results = await resp.json()
                if results and isinstance(results, list):
                    movie = results[0]
                    payload = {
                        "title": movie['title'], 
                        "qualityProfileId": 5, 
                        "titleSlug": movie['titleSlug'],
                        "images": movie['images'], 
                        "tmdbId": movie['tmdbId'], 
                        "year": movie['year'],
                        "rootFolderPath": "/movies", 
                        "monitored": True, 
                        "addOptions": {"searchForMovie": True}
                    }

                    add_url = f"{RADARR_URL}/movie?apiKey={RADARR_API_KEY}"
                    async with session.post(add_url, json=payload, timeout=15) as post_resp:
                        post_data = await post_resp.json()
                        if post_resp.status in [200, 201]:
                            return f"✅ Film '{movie['title']}' ajouté à Radarr (Ultra-HD)."
                        elif post_resp.status == 400 and any(err.get('errorCode') == 'MovieExistsValidator' for err in post_data):
                            return f"ℹ️ '{movie['title']}' est déjà dans Radarr."
                        else:
                            return f"Erreur lors de l'ajout du film (Code {post_resp.status})."
                return "❌ Film non trouvé."
    except Exception as e:
        return f"❌ Erreur technique Radarr : {e}"

# --- OUTILS (SONARR) ---
async def add_series_sonarr(title):
    search_url = f"{SONARR_URL}/series/lookup?term={title}&apiKey={SONARR_API_KEY}"
    logger.info(f"Searching for series: {title}")

    try:
        async with aiohttp.ClientSession() as session:
            async with session.get(search_url, timeout=15) as resp:
                if resp.status != 200:
                    return f"Erreur Sonarr (Code {resp.status})."

                results = await resp.json()
                if results and isinstance(results, list):
                    series = results[0]
                    payload = {
                        "title": series['title'],
                        "qualityProfileId": 5,
                        "titleSlug": series['titleSlug'],
                        "images": series['images'],
                        "tvdbId": series['tvdbId'],
                        "year": series['year'],
                        "rootFolderPath": "/tv",
                        "monitored": True,
                        "addOptions": {"searchForMissingEpisodes": True}
                    }

                    add_url = f"{SONARR_URL}/series?apiKey={SONARR_API_KEY}"
                    async with session.post(add_url, json=payload, timeout=15) as post_resp:
                        post_data = await post_resp.json()
                        if post_resp.status in [200, 201]:
                            return f"✅ Série '{series['title']}' ajoutée à Sonarr (Ultra-HD)."
                        elif post_resp.status == 400 and any(err.get('errorCode') == 'SeriesExistsValidator' for err in (post_data if isinstance(post_data, list) else [post_data])):
                            return f"ℹ️ '{series['title']}' est déjà dans Sonarr."
                        else:
                            return f"Erreur lors de l'ajout de la série (Code {post_resp.status})."
                return "❌ Série non trouvée."
    except Exception as e:
        return f"❌ Erreur technique Sonarr : {e}"

# --- LOGIQUE IA ---
async def process_ai_logic(prompt: str):
    logger.info(f"AI Processing: {prompt}")

    # Intent Detection with OpenAI
    headers = {"Authorization": f"Bearer {OPENAI_API_KEY}", "Content-Type": "application/json"}
    payload = {
        "model": "gpt-4o-mini",
        "messages": [
            {"role": "system", "content": "Tu es un assistant domotique. Détecte si l'utilisateur veut ajouter un FILM (Radarr) ou une SÉRIE (Sonarr). Réponds uniquement en JSON format: {\"type\": \"movie\"|\"series\"|\"chat\", \"query\": \"nom du media\"}"},
            {"role": "user", "content": prompt}
        ],
        "response_format": {"type": "json_object"}
    }

    try:
        async with aiohttp.ClientSession() as session:
            async with session.post(OPENAI_URL, json=payload, headers=headers) as resp:
                data = await resp.json()
                intent = json.loads(data["choices"][0]["message"]["content"])

                if intent["type"] == "movie":
                    return await add_movie_radarr(intent["query"])
                elif intent["type"] == "series":
                    return await add_series_sonarr(intent["query"])
                else:
                    # Chat normal
                    chat_payload = {
                        "model": "gpt-4o-mini",
                        "messages": [{"role": "user", "content": prompt}]
                    }
                    async with session.post(OPENAI_URL, json=chat_payload, headers=headers) as chat_resp:
                        chat_data = await chat_resp.json()
                        return chat_data["choices"][0]["message"]["content"]
    except Exception as e:
        logger.error(f"AI Logic Error: {e}")
        return "❌ Désolé, j'ai eu un problème pour traiter votre demande."

# --- WHATSAPP (ENVOI) ---
async def send_whatsapp(number, text):
    url = f"{WUZAPI_URL}/chat/send/text"
    headers = {"Token": WUZAPI_TOKEN, "Content-Type": "application/json"}
    clean_number = number.split('@')[0].split(':')[0]
    payload = {"phone": clean_number, "body": text}
    try:
        async with aiohttp.ClientSession() as session:
            await session.post(url, json=payload, headers=headers)
    except Exception as e:
        logger.error(f"WhatsApp send error: {e}")

# --- WEBHOOK ---
@app.post("/webhook")
async def webhook(request: Request, background_tasks: BackgroundTasks):
    try:
        data = await request.json()
        if data.get("type") == "Message":
            event = data.get("event") or json.loads(data.get("jsonData", "{}")).get("event")
            if not event: return {"status": "no_data"}

            info = event.get("Info", {})
            if not (info.get("IsFromMe") or AUTHORIZED_NUMBER in (info.get("Sender") or "")):
                return {"status": "unauthorized"}

            text = event.get("Message", {}).get("conversation") or \
                   event.get("Message", {}).get("extendedTextMessage", {}).get("text")

            if text:
                background_tasks.add_task(handle_and_reply, info.get("Sender") or info.get("Chat"), text)
    except Exception as e:
        logger.error(f"Webhook error: {e}")
    return {"status": "ok"}

async def handle_and_reply(sender, text):
    reply = await process_ai_logic(text)
    await send_whatsapp(sender, reply)

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="0.0.0.0", port=5000)

