    @bot.message_handler(commands=['status'])
    def status_cmd(m):
        if not is_authorized(m.from_user.id): return
        fmt = lambda u: f"{u.used/(1024**3):.1f}/{u.total/(1024**3):.1f}Go ({int(u.used/u.total*100)}%)"
        try:
            nvme = shutil.disk_usage('/')
            msg = f"üìä **Statut Stockage**\n\nüöÄ **NVMe** : `{fmt(nvme)}`"
            if os.path.exists('/mnt/externe'):
                hdd = shutil.disk_usage('/mnt/externe')
                msg += f"\nüìö **HDD** : `{fmt(hdd)}`"
            else:
                msg += f"\n‚ö†Ô∏è **HDD** : `Non d√©tect√© sur /mnt/externe`"
            bot.reply_to(m, msg, parse_mode='Markdown')
        except Exception as e:
            bot.reply_to(m, f"‚ùå Erreur lecture disques : {e}")

    @bot.message_handler(commands=['admin'])
    def admin_menu(m):
        if not is_authorized(m.from_user.id): return
        markup = types.InlineKeyboardMarkup(row_width=1)
        markup.add(types.InlineKeyboardButton("üìÇ Partage", callback_data="adm:share"),
                   types.InlineKeyboardButton("üë• Utilisateurs", callback_data="adm:users"),
                   types.InlineKeyboardButton("üßπ Nettoyage SSD", callback_data="adm:clean"),
                   types.InlineKeyboardButton("üîô Fermer", callback_data="adm:close"))
        bot.send_message(m.chat.id, "üõ†Ô∏è **Menu Administrateur**", reply_markup=markup, parse_mode='Markdown')

    def process_user_add(m):
        try:
            uid = int(m.text)
            with open(USERS_FILE, 'r') as f: users = json.load(f)["allowed"]
            if uid not in users:
                users.append(uid); save_users(users)
                bot.reply_to(m, f"‚úÖ ID {uid} ajout√©.")
            else: bot.reply_to(m, "‚ÑπÔ∏è D√©j√† autoris√©.")
        except: bot.reply_to(m, "‚ùå ID invalide.")

    @bot.callback_query_handler(func=lambda call: call.data.startswith("adm:"))
