            markup = types.InlineKeyboardMarkup()
            markup.add(types.InlineKeyboardButton("üóëÔ∏è Supprimer", callback_data=f"m_act:del:{d[1]}:{d[2]}"),
                       types.InlineKeyboardButton("üîó Partager", callback_data=f"m_act:share:{d[1]}:{d[2]}"),
                       types.InlineKeyboardButton("‚¨ÖÔ∏è Retour", callback_data=f"m_pag:{d[1]}:0"))
            bot.edit_message_text(f"üéØ **Action : {display_title}**", call.message.chat.id, call.message.message_id, reply_markup=markup, parse_mode='Markdown')
        elif d[0] == "m_act":
            action, cat, item_id = d[1], d[2], d[3]
            if action == "share":
                bot.answer_callback_query(call.id, "‚è≥ G√©n√©ration du lien...")
                cfg = RADARR_CFG if cat == "films" else SONARR_CFG
                endpoint = "movie" if cat == "films" else "series"
                try:
                    r = requests.get(f"{cfg['url']}/api/v3/{endpoint}/{item_id}", headers={"X-Api-Key": cfg['key']}, timeout=5)
                    if r.status_code == 200:
                        path = r.json().get('path')
