                    { "color": "red", "value": 90 }
                ]
            }
        },
        "overrides": []
    },
    "options": {
        "displayMode": "gradient",
        "orientation": "horizontal",
        "reduceOptions": {
            "calcs": ["lastNotNull"],
            "fields": "",
            "values": False
        },
        "showUnfilled": True
    }
}

