                ],
                "options": {"textMode": "value_and_name", "colorMode": "background", "graphMode": "area"},
                "fieldConfig": {"defaults": {"unit": "decbytes"}}
            },
            {
                "title": "Stockage HDD (/mnt/externe) : Utilisé / Total",
                "type": "stat",
                "gridPos": {"h": 6, "w": 12, "x": 12, "y": 18},
                "datasource": {"type": "prometheus", "uid": DS_UID},
                "targets": [
                    {"expr": "node_filesystem_size_bytes{mountpoint='/mnt/externe'} - node_filesystem_avail_bytes{mountpoint='/mnt/externe'}", "legendFormat": "Utilisé", "refId": "A"},
