import os
import shutil
import time
import logging
from pathlib import Path

# --- CONFIGURATION GLOBALE ---
SHARE_DIR = "/home/jules/media_share"
NVME_PATH = "/home/jules/infra/ai/data/media"  # Base NVMe pour Sonarr/Radarr
HDD_PATH = "/mnt/externe"                      # Base HDD d'archive
LOG_FILE = "/tmp/cleanup_share.log"
TARGET_NVME_PERCENT = 50.0
MAX_AGE_SECONDS = 24 * 3600  # 24 heures

logging.basicConfig(
    filename=LOG_FILE,
    level=logging.INFO,
    format='%(asctime)s - %(levelname)s - %(message)s'
)

def purge_share_directory():
    """Supprime les fichiers et symlinks de plus de 24h dans SHARE_DIR."""
    logging.info(f"D√©but de la purge de {SHARE_DIR}")
    if not os.path.exists(SHARE_DIR):
        logging.warning(f"Le dossier {SHARE_DIR} n'existe pas.")
        return

    now = time.time()
    for root, dirs, files in os.walk(SHARE_DIR):
        for name in files + dirs:
            path = os.path.join(root, name)
            try:
                # Utiliser lstat pour ne pas suivre les symlinks
                stat = os.lstat(path)
                # V√©rifier mtime ou ctime
                if now - stat.st_mtime > MAX_AGE_SECONDS:
                    if os.path.islink(path) or os.path.isfile(path):
                        os.unlink(path)
                        logging.info(f"Supprim√© (fichier/lien) : {path}")
                    elif os.path.isdir(path):
                        shutil.rmtree(path)
                        logging.info(f"Supprim√© (dossier) : {path}")
            except Exception as e:
                logging.error(f"Erreur lors de la suppression de {path} : {e}")

def get_nvme_usage_percent():
    """Retourne le pourcentage d'utilisation de la partition NVMe."""
    try:
        usage = shutil.disk_usage(NVME_PATH)
        return (usage.used / usage.total) * 100
    except Exception as e:
        logging.error(f"Erreur lecture usage NVMe : {e}")
        return 100.0  # Failsafe pour ne pas archiver si erreur

def is_hdd_mounted():
    """V√©rifie si le HDD de destination est accessible."""
    return os.path.ismount(HDD_PATH) or os.path.ismount("/mnt/externe") or os.path.exists(HDD_PATH)

def auto_tiering_nvme_to_hdd():
    """D√©place les fichiers les plus anciens du NVMe vers le HDD si > TARGET_NVME_PERCENT."""
    logging.info("V√©rification de l'espace NVMe pour Auto-Tiering...")

    if not is_hdd_mounted():
        logging.error(f"Le HDD cible ({HDD_PATH}) n'est pas mont√© ou accessible. Abandon du Tiering.")
        return

    usage_percent = get_nvme_usage_percent()
    logging.info(f"Utilisation NVMe actuelle : {usage_percent:.2f}% (Cible: {TARGET_NVME_PERCENT}%)")

    if usage_percent <= TARGET_NVME_PERCENT:
        logging.info("Espace suffisant. Aucun tiering n√©cessaire.")
        return

    # Lister tous les fichiers m√©dias sur le NVMe
    media_files = []
    extensions = {'.mkv', '.mp4', '.avi'}

    try:
        for root, _, files in os.walk(NVME_PATH):
            for file in files:
                ext = os.path.splitext(file)[1].lower()
                if ext in extensions:
                    path = os.path.join(root, file)
                    # Exclure les symlinks d√©j√† existants
                    if not os.path.islink(path):
                        stat = os.stat(path)
                        media_files.append((path, stat.st_atime, stat.st_size))
    except Exception as e:
        logging.error(f"Erreur lors du scan du NVMe : {e}")
        return

    # Trier par atime (dernier acc√®s) le plus ancien en premier
    media_files.sort(key=lambda x: x[1])

    for file_path, _, size in media_files:
        if get_nvme_usage_percent() <= TARGET_NVME_PERCENT:
            logging.info("Cible d'utilisation NVMe atteinte. Fin de la migration.")
            break

        rel_path = os.path.relpath(file_path, NVME_PATH)
        dest_path = os.path.join(HDD_PATH, rel_path)
        dest_dir = os.path.dirname(dest_path)

        logging.info(f"Migration de {file_path} vers {dest_path}")

        try:
            os.makedirs(dest_dir, exist_ok=True)
            # D√©placement physique
            shutil.move(file_path, dest_path)

            # Cr√©ation du symlink pour tromper Sonarr/Radarr
            os.symlink(dest_path, file_path)
            logging.info(f"Migration r√©ussie : Symlink cr√©√© sur le NVMe.")

        except Exception as e:
            logging.error(f"√âchec de la migration pour {file_path} : {e}")
            # Failsafe: tenter de restaurer si √ßa a plant√© en cours de route
            if os.path.exists(dest_path) and not os.path.exists(file_path):
                 try:
                     shutil.move(dest_path, file_path)
                     logging.info(f"Rollback r√©ussi pour {file_path}")
                 except:
                     pass

def main():
    logging.info("=== Lancement du Garbage Collector & Tiering ===")
    purge_share_directory()
    auto_tiering_nvme_to_hdd()
    logging.info("=== Fin de l'op√©ration ===")

if __name__ == "__main__":
    main()

import telebot, json, requests, re, logging, os
from telebot.types import InlineKeyboardMarkup, InlineKeyboardButton
from config import *

def stream_gpt_json(query):
    headers = {"Content-Type": "application/json", "Authorization": f"Bearer {OPENAI_KEY}"}
    prompt = f"Analyse : '{query}'. Renvoie JSON : {{\"titre\": \"str\", \"type\": \"serie|film\"}}"
    try:
        res = requests.post("https://api.openai.com/v1/chat/completions", headers=headers, json={"model": "gpt-4o-mini", "messages": [{"role": "user", "content": prompt}], "temperature": 0.1}, timeout=10)
        return res.json()['choices'][0]['message']['content']
    except: return None

def register_search_handlers(bot, is_authorized):
    @bot.message_handler(commands=['get'])
    def handle_get(m):
        if not is_authorized(m.from_user.id): return
        parts = m.text.split(' ', 1)
        if len(parts) > 1: process_get_request(bot, m, parts[1])
        else: bot.reply_to(m, "üé¨ Que veux-tu ? (Ex: /get Titanic)")

    @bot.message_handler(func=lambda m: True)
    def handle_ai_request(m):
        if not is_authorized(m.chat.id): return
        if m.text.startswith('/'): return
        process_get_request(bot, m, m.text)

    @bot.callback_query_handler(func=lambda call: call.data.startswith("dl_sel:"))
    def download_callback_handler(call):
        if not is_authorized(call.from_user.id): return
        d = call.data.split(":")
        cat, idx = d[1], int(d[2])
        try:
            with open(CONTEXT_FILE, "r") as f: ctx = json.load(f)
            search_data = ctx.get(f"{call.message.chat.id}_search")
            if not search_data:
                bot.answer_callback_query(call.id, "‚ùå Session expir√©e.")
                return

            item = search_data['results'][idx]
            cfg = RADARR_CFG if cat == "movie" else SONARR_CFG
            endpoint = "movie" if cat == "movie" else "series"
            id_key = "tmdbId" if cat == "movie" else "tvdbId"

            payload = {
                "title": item['title'],
                "qualityProfileId": 1,
                "titleSlug": item['titleSlug'],
                "images": item['images'],
                "rootFolderPath": "/movies" if cat == "movie" else "/tv",
                "monitored": True,
                "addOptions": {"searchForMovie": True} if cat == "movie" else {"searchForMissingEpisodes": True}
            }
            payload[id_key] = item[id_key]
            if cat == "series": payload["languageProfileId"] = 1

            r = requests.post(f"{cfg['url']}/api/v3/{endpoint}", headers={"X-Api-Key": cfg['key']}, json=payload, timeout=10)
            if r.status_code in [200, 201]:
                bot.edit_message_text(f"‚úÖ **Ajout√© :** `{item['title']} ({item.get('year')})`", call.message.chat.id, call.message.message_id, parse_mode='Markdown')
            else:
                bot.answer_callback_query(call.id, f"‚ùå Erreur {r.status_code}")
        except Exception as e:
            logging.error(f"DL Error: {e}")
            bot.answer_callback_query(call.id, "‚ùå Erreur interne.")

def process_get_request(bot, m, query):
    bot.send_chat_action(m.chat.id, 'typing')
    ai_res = stream_gpt_json(query)
    try:
        data = json.loads(re.search(r'\{.*\}', ai_res, re.DOTALL).group(0))
        titre = data['titre']; cat = "movie" if data['type'] == "film" else "series"
    except: titre = query; cat = "movie"
    cfg = RADARR_CFG if cat == "movie" else SONARR_CFG
    try:
        r = requests.get(f"{cfg['url']}/api/v3/{cat}/lookup?term={titre}", headers={"X-Api-Key": cfg['key']}, timeout=5)
        results = r.json()
        if not results: bot.send_message(m.chat.id, "‚ùå Aucun r√©sultat."); return
        ctx = {}
        if os.path.exists(CONTEXT_FILE):
            try: ctx = json.load(open(CONTEXT_FILE))
            except: ctx = {}
        ctx[f"{m.chat.id}_search"] = {"cat": cat, "results": results[:5]}
        with open(CONTEXT_FILE, "w") as f: json.dump(ctx, f)
        text = f"üîç **R√©sultats pour '{titre}'**\n\n"
        limit = min(len(results), 5)
        for i, res in enumerate(results[:limit], 1): text += f"{i}. {res['title']} ({res.get('year', 'N/A')})\n"
        markup = InlineKeyboardMarkup()
        btns = [InlineKeyboardButton(str(i), callback_data=f"dl_sel:{cat}:{i-1}") for i in range(1, limit+1)]
        markup.add(*btns)
        bot.send_message(m.chat.id, text, reply_markup=markup, parse_mode='Markdown')
    except: bot.send_message(m.chat.id, "‚ùå Erreur.")

import os, shutil, sys

NVME_DIR = "/data/media/films"
HDD_DIR = "/mnt/externe/Films"

def get_size(p):
    s = 0
    try:
        if os.path.isfile(p): s = os.path.getsize(p)
        else:
            for r, d, f in os.walk(p):
                for file in f: s += os.path.getsize(os.path.join(r, file))
    except: return 0
    return round(s / (1024**3), 2)

def list_items(path):
    if not os.path.exists(path): return f"‚ùå {path} absent."
    items = [f for f in os.listdir(path) if not f.startswith('.')]
    out = f"üìÇ **{os.path.basename(path)}** :\n"
    for i in sorted(items):
        sz = get_size(os.path.join(path, i))
        slug = i.replace(" ", "_")[:20]
        out += f"‚Ä¢ {i} ({sz}G)\n  [/arc_{slug}] [/del_{slug}]\n"
    return out

if __name__ == "__main__":
    arg = sys.argv[1] if len(sys.argv) > 1 else ""
    if arg == "nvme": print(list_items(NVME_DIR))
    elif arg == "hdd": print(list_items(HDD_DIR))
    else: print("Commande re√ßue : " + arg)

import os
import shutil
import sys

NVME_BUFFER = "/data/media/buffer"
HDD_BASE = "/mnt/externe"
PATHS = {"movies": f"{HDD_BASE}/Films", "series": f"{HDD_BASE}/Series"}

def list_media(category="movies"):
    path = PATHS.get(category)
    if not os.path.exists(path): return f"‚ùå Dossier {category} introuvable."
    items = [f for f in os.listdir(path) if not f.startswith('.')]
    return f"üé• **{category.capitalize()} :**\n" + "\n".join([f"‚Ä¢ {i}" for i in sorted(items)]) if items else f"üìÇ Aucun contenu dans {category}."

def get_status():
    report = "üìä **√âtat du Stockage :**\n"
    for name, path in [("üöÄ NVMe", "/"), ("üìö HDD", HDD_BASE)]:
        usage = shutil.disk_usage(path)
        report += f"{name} : {(usage.used/usage.total)*100:.1f}% ({usage.free//(2**30)} Go libres)\n"
    return report

if __name__ == "__main__":
    if len(sys.argv) > 1:
        cmd = sys.argv[1]
        if cmd == "list_media" and len(sys.argv) > 2: print(list_media(sys.argv[2]))
        elif cmd == "get_status": print(get_status())

import os, shutil, sys, json, re, subprocess

DOCKER_MODE = os.environ.get("DOCKER_MODE") == "true"
NVME_MOVIES = "/data/internal/media/movies" if DOCKER_MODE else "/home/jules/infra/ai/data/media/movies"
NVME_TV = "/data/internal/media/tv" if DOCKER_MODE else "/home/jules/infra/ai/data/media/tv"
HDD_ROOT = "/data/external" if DOCKER_MODE else "/mnt/externe"

PATHS = {
    "movies": {"nvme": [NVME_MOVIES], "hdd": f"{HDD_ROOT}/Films"},
    "series": {"nvme": [NVME_TV], "hdd": f"{HDD_ROOT}/Series"}
}
MIN_SIZE_BYTES = 500 * 1024 * 1024

def clean_media_name(name):
    name = os.path.splitext(name)[0]
    name = re.sub(r'[\._]', ' ', name)
    match = re.search(r'(.*?)\s*[\(\[]?((?:19|20)\d{2})[\)\]]?(.*)', name, re.IGNORECASE)
    title = match.group(1).strip() if match else name
    year = match.group(2) if match else ""
    tags = [r'\b\d{3,4}p\b', r'\b2160p\b', r'\b4k\b', r'\bHEVC\b', r'\bx26[45]\b', r'\bBluRay\b', r'\bWEB-DL\b', r'\bCOMPLETE\b', r'\bSeason\s*\d*\b']
    clean = f"{title} ({year})" if year else title
    for tag in tags: clean = re.sub(tag, '', clean, flags=re.IGNORECASE)
    return re.sub(r'\s+', ' ', clean).strip()

def get_size(p):
    try:
        if os.path.isfile(p): return os.path.getsize(p)
        return sum(os.path.getsize(os.path.join(r, d, f)) for r, d, fs in os.walk(p) for f in fs)
    except: return 0

def find_local_media(query):
    """Cherche un m√©dia sur les disques et renvoie les infos techniques."""
    results = []
    q = query.lower().replace(" ", "")
    for cat in PATHS:
        for d, is_nvme in [(p, True) for p in PATHS[cat]["nvme"]] + [(PATHS[cat]["hdd"], False)]:
            if not os.path.exists(d): continue
            for item in os.listdir(d):
                if q in item.lower().replace(" ", "").replace(".", ""):
                    path = os.path.join(d, item)
                    results.append({
                        "name": item,
                        "clean_name": clean_media_name(item),
                        "path": path,
                        "is_nvme": is_nvme,
                        "size_gb": round(get_size(path) / (1024**3), 2),
                        "category": cat
                    })
    return results

def get_media_list(category):
    media = []
    conf = PATHS.get(category)
    if not conf: return []
    for d, is_nvme in [(p, True) for p in conf["nvme"]] + [(conf["hdd"], False)]:
        if not os.path.exists(d): continue
        for f in os.listdir(d):
            if f.startswith('.'): continue
            path = os.path.join(d, f); size = get_size(path)
            if size < MIN_SIZE_BYTES: continue
            media.append({"name": clean_media_name(f), "real_name": f, "size_gb": round(size / (1024**3), 2), "is_nvme": is_nvme, "path": path})
    return sorted(media, key=lambda x: x['size_gb'], reverse=True)

