import telebot, subprocess, os, json, requests, logging, re, time, threading, shutil, pathlib
from datetime import datetime, timedelta
from telebot import types
from telebot.types import InlineKeyboardMarkup, InlineKeyboardButton
from flask import Flask, request
import share_engine

logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s - %(message)s')

# --- CONFIGURATION ---
TOKEN = "8514178494:AAG6QqF_q7F8wgAjeTUrGo4GzY33sBLY0jk"
SUPER_ADMIN = 6721936515
USERS_FILE = "/home/jules/scripts/users.json"
CONTEXT_FILE = "/tmp/bot_context.json"
SCRIPT_PATH = "/home/jules/scripts/media_manager.py"
RADARR_CFG = {"url": "http://localhost:7878", "key": "75ed9e2e3922442c8c1e012e47bc0906"}
SONARR_CFG = {"url": "http://localhost:8989", "key": "dfa7da413b48480097f6a399080be393"}
OPENAI_KEY = "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"

bot = telebot.TeleBot(TOKEN)

# --- UTILS & STORAGE ---
def load_allowed_users():
    try: return json.load(open(USERS_FILE))["allowed"]
    except: return [SUPER_ADMIN]

def is_authorized(uid): return uid in load_allowed_users()

def save_users(users):
    with open(USERS_FILE, "w") as f:
        json.dump({"allowed": users}, f)

def get_storage_emoji(item):
    if not item.get('hasFile') and item.get('statistics', {}).get('episodeFileCount', 0) == 0: return "üî¥"
    path = item.get('path', '')
    return "üìö" if path and "/mnt/externe" in path else "üöÄ"

# --- ADMIN HANDLERS ---
@bot.message_handler(commands=['admin'])
def admin_main(m):
    if m.from_user.id != SUPER_ADMIN: return
    markup = InlineKeyboardMarkup(row_width=1)
    markup.add(InlineKeyboardButton("üìÇ Partage", callback_data="adm:share"),
               InlineKeyboardButton("üë• Utilisateurs", callback_data="adm:users"),
               InlineKeyboardButton("üßπ Nettoyage SSD", callback_data="adm:clean"),
               InlineKeyboardButton("üîô Fermer", callback_data="adm:close"))
    bot.send_message(m.chat.id, "üõ†Ô∏è **Menu Administrateur**", reply_markup=markup, parse_mode='Markdown')

def process_user_add(m):
    try:
        uid = int(m.text)
        users = load_allowed_users()
        if uid not in users:
            users.append(uid); save_users(users)
            bot.reply_to(m, f"‚úÖ ID {uid} ajout√©.")
        else: bot.reply_to(m, "‚ÑπÔ∏è D√©j√† autoris√©.")
    except: bot.reply_to(m, "‚ùå ID invalide.")

# --- MEDIA ENGINE (ORIGINAL) ---
def list_media_unified(m, cat, title, is_edit=False):
    res = subprocess.run(["python3", SCRIPT_PATH, "json_list", cat], capture_output=True, text=True)
    try:
        items = json.loads(res.stdout); ctx = {}
        if os.path.exists(CONTEXT_FILE):
            try: ctx = json.load(open(CONTEXT_FILE))
            except: ctx = {}
        ctx[str(m.chat.id)] = {"category": cat, "list": items}
        with open(CONTEXT_FILE, "w") as f: json.dump(ctx, f)
    except: return
    if not items:
        msg = f"üìÇ Aucun(e) {cat} trouv√©(e)."
        if is_edit: bot.edit_message_text(msg, m.chat.id, m.message_id)
        else: bot.send_message(m.chat.id, msg)
        return
    text = f"üé¨ {title} : {cat}\n\n"
    for i, f in enumerate(items, 1): text += f"{i}. {f['name']} ({f['size_gb']}G)\n"
    markup = InlineKeyboardMarkup(row_width=5)
    markup.add(*[InlineKeyboardButton(str(i), callback_data=f"sel:{cat}:{i-1}") for i in range(1, len(items)+1)])
    markup.add(InlineKeyboardButton("‚¨ÖÔ∏è Retour", callback_data="adm:share_owned"))
    if is_edit: bot.edit_message_text(text, m.chat.id, m.message_id, reply_markup=markup)
    else: bot.send_message(m.chat.id, text, reply_markup=markup)

# --- PAGED MEDIA UI (LIVE API) ---
def show_media_list(chat_id, message_id, category, page=0, is_new=False):
    cfg = RADARR_CFG if category == "films" else SONARR_CFG
    endpoint = "movie" if category == "films" else "series"
    try:
        r = requests.get(f"{cfg['url']}/api/v3/{endpoint}", headers={"X-Api-Key": cfg['key']}, timeout=5)
        r.raise_for_status(); data = r.json()
        if category == "films": owned = [f for f in data if f.get('hasFile') is True]
        else: owned = [s for s in data if s.get('statistics', {}).get('episodeFileCount', 0) > 0]
        owned.sort(key=lambda x: x.get('added', ''), reverse=True)
        total = len(owned); start, end = page * 10, (page + 1) * 10
        items = owned[start:end]
        if not items: bot.send_message(chat_id, "üìÇ Vide."); return
        text = f"{'üé¨' if category == 'films' else 'üì∫'} **{category.capitalize()}** ({total})\n\n"
        for i, item in enumerate(items, 1):
            text += f"{start + i}. {get_storage_emoji(item)} {item['title']} ({item.get('year', '')})\n"
        markup = InlineKeyboardMarkup(row_width=5)
        btns = [InlineKeyboardButton(str(start+i), callback_data=f"m_sel:{category}:{items[i-1]['id']}") for i in range(1, len(items)+1)]
        markup.add(*btns)
        nav = []
        if page > 0: nav.append(InlineKeyboardButton("‚¨ÖÔ∏è", callback_data=f"m_pag:{category}:{page-1}"))
        if end < total: nav.append(InlineKeyboardButton("‚û°Ô∏è", callback_data=f"m_pag:{category}:{page+1}"))
        if nav: markup.row(*nav)
        markup.add(InlineKeyboardButton("üè† Menu Principal", callback_data="adm:main"))
        if is_new: bot.send_message(chat_id, text, reply_markup=markup, parse_mode='Markdown')
        else: bot.edit_message_text(text, chat_id, message_id, reply_markup=markup, parse_mode='Markdown')
    except: bot.send_message(chat_id, "‚ùå Erreur API.")

@bot.message_handler(commands=['films'])
def films_cmd(m):
    if is_authorized(m.chat.id): show_media_list(m.chat.id, None, "films", 0, True)

@bot.message_handler(commands=['series'])
def series_cmd(m):
    if is_authorized(m.chat.id): show_media_list(m.chat.id, None, "series", 0, True)

# --- SEARCH ENGINE (/GET) ---
def stream_gpt_json(query):
    headers = {"Content-Type": "application/json", "Authorization": f"Bearer {OPENAI_KEY}"}
    prompt = f"Analyse : '{query}'. Renvoie JSON : {{\"titre\": \"str\", \"type\": \"serie|film\"}}"
    try:
        res = requests.post("https://api.openai.com/v1/chat/completions", headers=headers, json={"model": "gpt-4o-mini", "messages": [{"role": "user", "content": prompt}], "temperature": 0.1}, timeout=10)
        return res.json()['choices'][0]['message']['content']
    except: return None

@bot.message_handler(commands=['get'])
def handle_get(m):
    if not is_authorized(m.chat.id): return
    parts = m.text.split(' ', 1)
    if len(parts) > 1: process_get_request(m, parts[1])
    else: bot.reply_to(m, "üé¨ Que veux-tu ?")

def process_get_request(m, query):
    bot.send_chat_action(m.chat.id, 'typing')
    ai_res = stream_gpt_json(query)
    try:
        data = json.loads(re.search(r'\{.*\}', ai_res, re.DOTALL).group(0))
        titre = data['titre']; cat = "movie" if data['type'] == "film" else "series"
    except: titre = query; cat = "movie"
    cfg = RADARR_CFG if cat == "movie" else SONARR_CFG
    try:
        r = requests.get(f"{cfg['url']}/api/v3/{cat}/lookup?term={titre}", headers={"X-Api-Key": cfg['key']}, timeout=5)
        results = r.json()
        if not results: bot.send_message(m.chat.id, "‚ùå Aucun r√©sultat."); return
        text = f"üîç **R√©sultats pour '{titre}'**\n\n"
        limit = min(len(results), 5)
        for i, res in enumerate(results[:limit], 1): text += f"{i}. {res['title']} ({res.get('year', 'N/A')})\n"
        markup = InlineKeyboardMarkup()
        btns = [InlineKeyboardButton(str(i), callback_data=f"dl_sel:{cat}:{i}") for i in range(1, limit+1)]
        markup.add(*btns)
        bot.send_message(m.chat.id, text, reply_markup=markup, parse_mode='Markdown')
    except: bot.send_message(m.chat.id, "‚ùå Erreur.")

# --- MASTER CALLBACK ROUTER ---
@bot.callback_query_handler(func=lambda call: True)
def master_router(call):
    if not is_authorized(call.from_user.id): return
    d = call.data.split(":")

    # 1. New Paged UI Callbacks
    if d[0] in ["m_pag", "m_sel", "m_act", "dl_sel"]:
        if d[0] == "m_pag": show_media_list(call.message.chat.id, call.message.message_id, d[1], int(d[2]))
        elif d[0] == "m_sel":
            markup = InlineKeyboardMarkup()
            markup.add(InlineKeyboardButton("üóëÔ∏è Supprimer", callback_data=f"m_act:del:{d[1]}:{d[2]}"),
