        item = results[0]
        found_title = item.get('title', titre)
        year = item.get('year', '')
        item_id = item.get(id_key)

        # Validation de Flux: V√©rification si d√©j√† dans Sonarr/Radarr
        r_check = requests.get(f"{cfg['url']}/api/v3/{endpoint}", headers={"X-Api-Key": cfg['key']}, timeout=10)
