            bot.edit_message_text("üõ†Ô∏è **Menu Administrateur**", call.message.chat.id, call.message.message_id, reply_markup=markup)
        elif cmd == "share":
            markup = InlineKeyboardMarkup(row_width=1)
            markup.add(InlineKeyboardButton("‚úÖ Contenu poss√©d√©", callback_data="adm:share_owned"), InlineKeyboardButton("‚ùå Contenu non poss√©d√©", callback_data="adm:share_new"), InlineKeyboardButton("‚¨ÖÔ∏è Retour", callback_data="adm:main"))
            bot.edit_message_text("üìÇ **Gestion du Partage**", call.message.chat.id, call.message.message_id, reply_markup=markup)
        elif cmd == "users":
            markup = InlineKeyboardMarkup(row_width=1)
            markup.add(InlineKeyboardButton("üìã Lister", callback_data="adm:users_list"), InlineKeyboardButton("‚ûï Ajouter", callback_data="adm:users_add"), InlineKeyboardButton("‚ùå R√©voquer", callback_data="adm:users_rev"), InlineKeyboardButton("‚¨ÖÔ∏è Retour", callback_data="adm:main"))
            bot.edit_message_text("üë• **Gestion des Utilisateurs**", call.message.chat.id, call.message.message_id, reply_markup=markup)
        elif cmd == "users_list":
            users = load_allowed_users(); text = "üìã **Utilisateurs :**\n\n" + "\n".join([f"‚Ä¢ `{u}`" for u in users])
            bot.edit_message_text(text, call.message.chat.id, call.message.message_id, reply_markup=InlineKeyboardMarkup().add(InlineKeyboardButton("‚¨ÖÔ∏è Retour", callback_data="adm:users")), parse_mode='Markdown')
        elif cmd == "users_add":
            msg = bot.send_message(call.message.chat.id, "üë§ ID Telegram :")
            bot.register_next_step_handler(msg, process_user_add)
        elif cmd == "users_rev":
            users = load_allowed_users(); markup = InlineKeyboardMarkup(row_width=1)
            for u in users:
                if u == SUPER_ADMIN: continue
                markup.add(InlineKeyboardButton(f"‚ùå {u}", callback_data=f"adm:users_del:{u}"))
            markup.add(InlineKeyboardButton("‚¨ÖÔ∏è Retour", callback_data="adm:users"))
            bot.edit_message_text("S√©lectionnez l'ID √† r√©voquer :", call.message.chat.id, call.message.message_id, reply_markup=markup)
        elif cmd == "users_del":
            uid = int(d[2]); users = load_allowed_users()
            if uid in users: users.remove(uid); save_users(users); bot.answer_callback_query(call.id, f"‚úÖ R√©voqu√©.")
            master_router(types.CallbackQuery(id=call.id, from_user=call.from_user, message=call.message, data="adm:users", chat_instance=call.chat_instance))
        elif cmd == "share_owned":
            markup = InlineKeyboardMarkup(row_width=2)
            markup.add(InlineKeyboardButton("üé¨ Films", callback_data="adm:list_owned:movies"), InlineKeyboardButton("üì∫ S√©ries", callback_data="adm:list_owned:series"), InlineKeyboardButton("‚¨ÖÔ∏è Retour", callback_data="adm:share"))
            bot.edit_message_text("Cat√©gorie :", call.message.chat.id, call.message.message_id, reply_markup=markup)
        elif cmd == "list_owned": list_media_unified(call.message, d[2], "Partage", True)
        elif cmd == "close": bot.delete_message(call.message.chat.id, call.message.message_id)
        return

    # 3. Media Engine Original Callbacks (sel, act, share_exec, share_final)
    if d[0] == "sel":
        try:
            with open(CONTEXT_FILE, "r") as f: ctx = json.load(f); item = ctx[str(call.message.chat.id)]["list"][int(d[2])]
            markup = InlineKeyboardMarkup(row_width=2)
            act = "arc" if item['is_nvme'] else "res"
            markup.add(InlineKeyboardButton("üì¶ Archiv/Rest", callback_data=f"act:{act}:{d[1]}:{d[2]}"), InlineKeyboardButton("üóëÔ∏è Supprimer", callback_data=f"act:del:{d[1]}:{d[2]}"))
            if call.from_user.id == SUPER_ADMIN: markup.add(InlineKeyboardButton("üîó Partager", callback_data=f"share_exec:{d[2]}"))
            markup.add(InlineKeyboardButton("‚ùå Annuler", callback_data="cancel"))
            bot.edit_message_text(f"üõ†Ô∏è {item['name']}", call.message.chat.id, call.message.message_id, reply_markup=markup)
        except: bot.answer_callback_query(call.id, "‚ùå Session expir√©e.")
    elif d[0] == "act":
        try:
            with open(CONTEXT_FILE, "r") as f: ctx = json.load(f); item = ctx[str(call.message.chat.id)]["list"][int(d[3])]
            res = subprocess.run(["python3", SCRIPT_PATH, "action", d[1], d[2], item['path']], capture_output=True, text=True)
            bot.edit_message_text(f"‚úÖ {item['name']}: {res.stdout.strip()}", call.message.chat.id, call.message.message_id)
        except: bot.answer_callback_query(call.id, "‚ùå Erreur.")
    elif d[0] == "share_exec":
        try:
            with open(CONTEXT_FILE, "r") as f: ctx = json.load(f); item = ctx[str(call.message.chat.id)]["list"][int(d[1])]
            src = pathlib.Path(item['path']); subtitles = list(src.parent.glob(f"{src.stem}*.srt"))
            if subtitles:
                markup = InlineKeyboardMarkup(row_width=1)
                markup.add(InlineKeyboardButton("‚ö° Symlink", callback_data=f"share_final:{d[1]}:link"), InlineKeyboardButton("üé¨ Remux", callback_data=f"share_final:{d[1]}:remux"), InlineKeyboardButton("‚ùå Annuler", callback_data="cancel"))
                bot.edit_message_text(f"üéØ **Options Partage**\n{len(subtitles)} sous-titres trouv√©s.", call.message.chat.id, call.message.message_id, reply_markup=markup)
            else: master_router(types.CallbackQuery(id=call.id, from_user=call.from_user, message=call.message, data=f"share_final:{d[1]}:link", chat_instance=call.chat_instance))
        except: bot.answer_callback_query(call.id, "‚ùå Erreur.")
    elif d[0] == "share_final":
