def get_storage_emoji(item):
    if not item.get('hasFile') and item.get('statistics', {}).get('episodeFileCount', 0) == 0: return "üî¥"
    path = item.get('path', '')
    return "üìö" if path and "/mnt/externe" in path else "üöÄ"

# --- ADMIN HANDLERS ---
@bot.message_handler(commands=['admin'])
def admin_main(m):
