# --- SEARCH ENGINE (/GET) ---
def stream_gpt_json(query):
    headers = {"Content-Type": "application/json", "Authorization": f"Bearer {OPENAI_KEY}"}
    prompt = f"Analyse : '{query}'. Renvoie JSON : {{\"titre\": \"str\", \"type\": \"serie|film\"}}"
    try:
        res = requests.post("https://api.openai.com/v1/chat/completions", headers=headers, json={"model": "gpt-4o-mini", "messages": [{"role": "user", "content": prompt}], "temperature": 0.1}, timeout=10)
        return res.json()['choices'][0]['message']['content']
    except: return None

@bot.message_handler(commands=['get'])
def handle_get(m):
    if not is_authorized(m.chat.id): return
    parts = m.text.split(' ', 1)
    if len(parts) > 1: process_get_request(m, parts[1])
    else: bot.reply_to(m, "üé¨ Que veux-tu ?")

def process_get_request(m, query):
    bot.send_chat_action(m.chat.id, 'typing')
    ai_res = stream_gpt_json(query)
    try:
        data = json.loads(re.search(r'\{.*\}', ai_res, re.DOTALL).group(0))
        titre = data['titre']; cat = "movie" if data['type'] == "film" else "series"
    except: titre = query; cat = "movie"
    cfg = RADARR_CFG if cat == "movie" else SONARR_CFG
    try:
        r = requests.get(f"{cfg['url']}/api/v3/{cat}/lookup?term={titre}", headers={"X-Api-Key": cfg['key']}, timeout=5)
        results = r.json()
        if not results: bot.send_message(m.chat.id, "‚ùå Aucun r√©sultat."); return
        text = f"üîç **R√©sultats pour '{titre}'**\n\n"
        limit = min(len(results), 5)
        for i, res in enumerate(results[:limit], 1): text += f"{i}. {res['title']} ({res.get('year', 'N/A')})\n"
        markup = InlineKeyboardMarkup()
        btns = [InlineKeyboardButton(str(i), callback_data=f"dl_sel:{cat}:{i}") for i in range(1, limit+1)]
        markup.add(*btns)
        bot.send_message(m.chat.id, text, reply_markup=markup, parse_mode='Markdown')
    except: bot.send_message(m.chat.id, "‚ùå Erreur.")

# --- MASTER CALLBACK ROUTER ---
@bot.callback_query_handler(func=lambda call: True)
def master_router(call):
    if not is_authorized(call.from_user.id): return
    d = call.data.split(":")

