            markup = InlineKeyboardMarkup()
            markup.add(InlineKeyboardButton("üóëÔ∏è Supprimer", callback_data=f"m_act:del:{d[1]}:{d[2]}"),
                       InlineKeyboardButton("üîó Partager", callback_data=f"m_act:share:{d[1]}:{d[2]}"),
                       InlineKeyboardButton("‚¨ÖÔ∏è Retour", callback_data=f"m_pag:{d[1]}:0"))
            bot.edit_message_text(f"üéØ **Action sur ID {d[2]}**", call.message.chat.id, call.message.message_id, reply_markup=markup)
        elif d[0] == "dl_sel": bot.answer_callback_query(call.id, "üì• Ajout√©.")
