import os
import shutil
import time
import logging
from pathlib import Path

# --- CONFIGURATION GLOBALE ---
SHARE_DIR = "/home/jules/media_share"
NVME_PATH = "/home/jules/infra/ai/data/media"  # Base NVMe pour Sonarr/Radarr
HDD_PATH = "/mnt/externe"                      # Base HDD d'archive
LOG_FILE = "/tmp/cleanup_share.log"
TARGET_NVME_PERCENT = 50.0
MAX_AGE_SECONDS = 24 * 3600  # 24 heures

logging.basicConfig(
    filename=LOG_FILE,
    level=logging.INFO,
    format='%(asctime)s - %(levelname)s - %(message)s'
)

def purge_share_directory():
    """Supprime les fichiers et symlinks de plus de 24h dans SHARE_DIR."""
    logging.info(f"D√©but de la purge de {SHARE_DIR}")
    if not os.path.exists(SHARE_DIR):
        logging.warning(f"Le dossier {SHARE_DIR} n'existe pas.")
        return

    now = time.time()
