    except: return
    if not items:
        msg = f"üìÇ Aucun(e) {cat} trouv√©(e)."
        if is_edit: bot.edit_message_text(msg, m.chat.id, m.message_id)
        else: bot.send_message(m.chat.id, msg)
        return
    text = f"üé¨ {title} : {cat}\n\n"
