    bot.send_chat_action(m.chat.id, 'typing')
    ai_res = stream_gpt_json(query)
    try:
        data = json.loads(re.search(r'\{.*\}', ai_res, re.DOTALL).group(0))
        titre = data['titre']; cat = "movie" if data['type'] == "film" else "series"
